import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { getGroups, deleteGroup } from "../api/api";

function Groups() {
  const queryClient = useQueryClient();
  const { data: groups = [], isLoading, error } = useQuery(["groups"], getGroups);

  const deleteMutation = useMutation(deleteGroup, {
    onSuccess: () => queryClient.invalidateQueries(["groups"]),
  });

  if (isLoading) return <p>Loading groups...</p>;
  if (error) return <p>Error loading groups</p>;

  return (
    <div>
      <h2>Groups</h2>
      <ul>
        {groups.map((g) => (
          <li key={g.id}>
            {g.name}
            <button onClick={() => deleteMutation.mutate(g.id)} style={{ marginLeft: "1rem" }}>
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Groups;
