import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { getGroups, deleteGroup } from "../api/api";

function Groups({ onSelectGroup }) {
  const queryClient = useQueryClient();

  const { data: groups = [], isLoading } = useQuery(["groups"], getGroups);

  const deleteMutation = useMutation(deleteGroup, {
    onSuccess: () => queryClient.invalidateQueries(["groups"]),
  });

  if (isLoading) return <p>Loading groups...</p>;

  return (
    <div>
      <h2>Groups</h2>
      <ul>
        {groups.map((group) => (
          <li key={group.id}>
            <button onClick={() => onSelectGroup(group)}>{group.name}</button>
            <button onClick={() => deleteMutation.mutate(group.id)} style={{ marginLeft: "1rem" }}>
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Groups;
