// src/components/Groups.js
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { getGroups, createGroup, deleteGroup } from "../api/api";
import { useState } from "react";

function Groups() {
  const queryClient = useQueryClient();
  const [name, setName] = useState("");

  // Fetch groups
  const { data: groups = [], isLoading, error } = useQuery({
    queryKey: ["groups"],
    queryFn: getGroups,
  });

  // Create group
  const createMutation = useMutation({
    mutationFn: createGroup,
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["groups"] });
      setName("");
    },
  });

  // Delete group
  const deleteMutation = useMutation({
    mutationFn: deleteGroup,
    onSuccess: () => queryClient.invalidateQueries({ queryKey: ["groups"] }),
  });

  const handleAddGroup = () => {
    if (!name) return alert("Enter group name");
    createMutation.mutate({ name, userIds: [] }); // Add selected users later
  };

  if (isLoading) return <p>Loading groups...</p>;
  if (error) return <p>Error loading groups: {error.message}</p>;

  return (
    <div>
      <h2>Groups</h2>
      <div>
        <input
          type="text"
          placeholder="Group Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <button onClick={handleAddGroup} disabled={createMutation.isLoading}>
          {createMutation.isLoading ? "Creating..." : "Create Group"}
        </button>
      </div>
      <ul style={{ marginTop: "1rem" }}>
        {groups.map((g) => (
          <li key={g.id}>
            {g.name}
            <button
              onClick={() => deleteMutation.mutate(g.id)}
              style={{ marginLeft: "1rem" }}
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Groups;
