import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { getExpenses, deleteExpense } from "../api/api";

function Expenses({ group }) {
  const queryClient = useQueryClient();

  const { data: expenses = [], isLoading } = useQuery(
    ["expenses", group.id],
    () => getExpenses(group.id),
    { enabled: !!group }
  );

  const deleteMutation = useMutation(deleteExpense, {
    onSuccess: () => queryClient.invalidateQueries(["expenses", group.id]),
  });

  if (!group) return <p>Select a group to see expenses.</p>;
  if (isLoading) return <p>Loading expenses...</p>;

  return (
    <div>
      <h2>Expenses for {group.name}</h2>
      <ul>
        {expenses.map((exp) => (
          <li key={exp.id}>
            {exp.description} â€” ${exp.amount} (Paid by {exp.paidBy?.name || "Unknown"})
            <button
              onClick={() => deleteMutation.mutate(exp.id)}
              style={{ marginLeft: "1rem" }}
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Expenses;
