// src/components/Users.js
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { getUsers, createUser, deleteUser } from "../api/api";
import { useState } from "react";

function Users() {
  const queryClient = useQueryClient();
  const [email, setEmail] = useState("");

  // Fetch users
  const { data: users = [], isLoading, error } = useQuery({
    queryKey: ["users"],
    queryFn: getUsers,
  });

  // Add user mutation (by existing email)
  const addUserMutation = useMutation({
    mutationFn: createUser,
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["users"] });
      setEmail("");
    },
    onError: (err) => alert(err.response?.data?.message || err.message),
  });

  // Delete user mutation
  const deleteMutation = useMutation({
    mutationFn: deleteUser,
    onSuccess: () => queryClient.invalidateQueries({ queryKey: ["users"] }),
    onError: (err) => alert(err.response?.data?.message || err.message),
  });

  const handleAddUser = () => {
    if (!email) return alert("Enter email");
    addUserMutation.mutate({ email });
  };

  if (isLoading) return <p>Loading users...</p>;
  if (error) return <p>Error loading users: {error.message}</p>;

  return (
    <div>
      <h2>Users</h2>
      <div>
        <input
          type="email"
          placeholder="User Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <button onClick={handleAddUser} disabled={addUserMutation.isLoading}>
          {addUserMutation.isLoading ? "Adding..." : "Add User"}
        </button>
      </div>
      <ul style={{ marginTop: "1rem" }}>
        {users.map((user) => (
          <li key={user.id}>
            {user.name} ({user.email})
            <button
              onClick={() => deleteMutation.mutate(user.id)}
              style={{ marginLeft: "1rem" }}
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Users;
