// src/api.js
import axios from "axios";

const API_BASE_URL = "http://localhost:8080/api";

// USERS
export const getUsers = () => axios.get(`${API_BASE_URL}/users`).then(res => res.data);
export const createUser = (user) => axios.post(`${API_BASE_URL}/users`, user).then(res => res.data);
export const deleteUser = (id) => axios.delete(`${API_BASE_URL}/users/${id}`);

// GROUPS
export const getGroups = () => axios.get(`${API_BASE_URL}/groups`).then(res => res.data);
export const createGroup = (group) => axios.post(`${API_BASE_URL}/groups`, group).then(res => res.data);
export const deleteGroup = (id) => axios.delete(`${API_BASE_URL}/groups/${id}`);

// EXPENSES
export const getExpenses = (groupId) =>
  axios.get(`${API_BASE_URL}/groups/${groupId}/expenses`).then(res => res.data);
export const createExpense = (groupId, expense) =>
  axios.post(`${API_BASE_URL}/groups/${groupId}/expenses`, expense).then(res => res.data);
export const deleteExpense = (id) => axios.delete(`${API_BASE_URL}/expenses/${id}`);
