// src/components/NewGroupForm.js
import { useState } from "react";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { createGroup, getUsers } from "../api/api";

function NewGroupForm() {
  const queryClient = useQueryClient();
  const [name, setName] = useState("");
  const [selectedUserIds, setSelectedUserIds] = useState([]); 

  // Fetch users directly here
  const { data: users = [], isLoading } = useQuery({
    queryKey: ["users"],
    queryFn: getUsers,
  });

  const mutation = useMutation({
    mutationFn: createGroup,
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["groups"] }); // refresh groups
      setName("");
      setSelectedUserIds("");
    },
  });

  const handleUserChange = (e) => {
    const options = Array.from(e.target.selectedOptions); // get all selected options
    const values = options.map((opt) => Number(opt.value)); // convert to numbers
    setSelectedUserIds(values);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!name) return;
    mutation.mutate({
      name,
      userIds: selectedUserIds, // send array of selected user IDs
    });
  };

  if (isLoading) return <p>Loading users...</p>;

  return (
    <form onSubmit={handleSubmit} style={{ marginBottom: "1rem" }}>
      <h3>Create Group</h3>
      <input
        type="text"
        placeholder="Group Name"
        value={name}
        onChange={(e) => setName(e.target.value)}
      />
      <select
        multiple
        value={selectedUserIds}
        onChange={handleUserChange}
        style={{ width: "200px", height: "100px" }} // optional styling
      >
        {users.map((user) => (
          <option key={user.id} value={user.id}>
            {user.name}
          </option>
        ))}
      </select>
      <button type="submit" disabled={mutation.isPending}>
        {mutation.isPending ? "Creating..." : "Create Group"}
      </button>
    </form>
  );
}

export default NewGroupForm;
